import React from 'react';


export class Timer extends React.Component{
    constructor(props){
        super(props)
        //this._child = React.createRef()
        this.state ={
        seconds: 0,
        minutes: 0,
        error: false,
        position: null,
        redirect: false
        }
        this.clear = this.clear.bind(this);
    }
    
    timerEx(){
        if(this.state.seconds === 0){
            this.setState({redirect : true, seconds : this.state.seconds + 1})
        }
        else if(this.state.minutes !== 0 || this.state.seconds !== 0){
            this.setState({seconds: 0})
            this.setState({redirect : false, seconds : this.state.seconds + 1})
        }
        
    }
    timer = () => setInterval(() =>{
        this.timerEx()
    }, 1000);

    componentWillMount(){
        this.setState({error : false});
        this.timeoutID = this.timer();
    }
    componentDidUpdate(prevProps, prevState, snapshot, timer){
        //clearTimeout(this.timeoutID);
        if(this.state.seconds === 60){
            this.setState({seconds: 0, minutes: this.state.minutes + 1});
        }
    }
    
    clear(){
        this.setState({seconds: 0, minutes: 0, error:false, position: null, redirect: false});
        clearInterval(this.timeoutID);
        this.timer();
        console.log("Clear ran");
    }

    render(){
        return <div>
                    <header clear = {this.clear}></header>
                    {this.state.minutes} minutes {this.state.seconds} seconds
                </div>
    }
}
