import React, {useState} from 'react';
import { Component } from 'react';

export class Timer extends React.Component{
    constructor(props){
        super(props)
        this.state ={
        seconds: 0,
        minutes: 0,
        error: false,
        position: null,
        redirect: false
        }
    }
    


    timer = () => setTimeout(() =>{
        this.setState({redirect : true, seconds : this.seconds + 1})
        if(this.seconds === 60){
            this.seconds = 0;
            this.minutes += 1;
        }
    }, 1000);

    componentWillMount(){
        this.setState({error : false});
        this.timeoutID = this.timer();
    }
    componentDidUpdate(prevProps, prevState, snapshot, timer){
        clearTimeout(this.timeoutID);
        this.timer();
    }
    
    render(){
        return <div>{this.props.minutes} minutes {this.props.seconds} seconds</div>
    }
}
