import React, {useState} from 'react';
import { Component } from 'react';



class Timer extends Component{
    constructor(props){
        super(props)
        this.state ={
        seconds: 0,
        minutes: 0,
        error: false,
        position: null,
        redirect: false
        }

        this.error = this.error.bind(this);
        this.position = this.position.bind(this);
        this.redirect = this.redirect.bind(this);
        this.seconds = this.seconds.bind(this);
        this.minutes = this.minutes.bind(this);
    }
    


    timer = () => setTimeout(() =>{
        this.setState(this.redirect=true, this.seconds+=1)
    }, 1000);

    componentWillMount(){
        this.setState(this.error = false);
        this.timeoutID = this.timer();
    }
    componentDidUpdate(prevProps, prevState, snapshot, timer){
        clearTimeout(this.timeoutID);
    }
}
